import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Statistics.css';

const Statistics = () => {
    const [stats, setStats] = useState([]);

    useEffect(() => {
        fetchStatistics();
    }, []);

    const fetchStatistics = () => {
        axios.get('http://localhost:3001/statistics')
            .then(response => {
                setStats(response.data);
            })
            .catch(error => {
                console.error('Error fetching statistics:', error);
            });
    };

    const handleDelete = (id) => {
        axios.delete(`http://localhost:3001/statistics/${id}`)
            .then(() => {
                fetchStatistics();
            })
            .catch(error => {
                console.error('Error deleting statistics:', error);
            });
    };

    return (
        <div>
            <h2>Analysis Statistics</h2>
            <table>
                <thead>
                <tr>
                    <th>ID</th>
                    <th>File Name</th>
                    <th>Language</th>
                    <th>Vulnerabilities Detected</th>
                    <th>Analysis Date</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {stats.map(stat => (
                    <tr key={stat.id}>
                        <td>{stat.id}</td>
                        <td>{stat.file_name}</td>
                        <td>{stat.language}</td>
                        <td>{stat.vulnerabilities_detected}</td>
                        <td>{stat.analysis_date}</td>
                        <td>
                            <button onClick={() => handleDelete(stat.id)}>Delete</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
            <Link to="/">
                <button>Back</button>
            </Link>
        </div>
    );
};

export default Statistics;
