import React, { useEffect, useRef } from 'react';
import hljs from 'highlight.js';
import 'highlight.js/styles/github-dark.css';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

const AnalysisResult = ({ result }) => {
    const codeRef = useRef();

    useEffect(() => {
        if (codeRef.current) {
            hljs.highlightElement(codeRef.current);
        }
    }, [result]);

    if (!result) {
        return null;
    }

    const getHighlightedCode = () => {
        const lines = result.fileContent.split('\n');
        result.issues.forEach(issue => {
            const lineIndex = issue.line - 1;
            if (lineIndex >= 0 && lineIndex < lines.length) {
                lines[lineIndex] = `<span style="background-color: yellow;">${lines[lineIndex]}</span>`;
            }
        });
        return lines.join('\n');
    };

    const handleExportPDF = () => {
        const doc = new jsPDF();
        doc.setFontSize(18);
        doc.text('Analysis Result', 14, 22);
        doc.setFontSize(12);
        doc.text('Code:', 14, 30);

        const splitCode = doc.splitTextToSize(result.fileContent, 180);
        doc.text(splitCode, 14, 36);

        if (result.issues.length > 0) {
            doc.setFontSize(12);
            doc.text('Issues:', 14, 36 + splitCode.length * 6);
            result.issues.forEach((issue, index) => {
                doc.text(`Line ${issue.line}: ${issue.issue}`, 14, 42 + splitCode.length * 6 + index * 6);
            });
        } else {
            doc.text('No issues found', 14, 42 + splitCode.length * 6);
        }

        doc.save('analysis_result.pdf');
    };

    return (
        <div>
            <h2>Analysis Result</h2>
            <pre>
                <code ref={codeRef} className="hljs" dangerouslySetInnerHTML={{ __html: getHighlightedCode() }} />
            </pre>
            <h3>Issues:</h3>
            {result.issues.length > 0 ? (
                <ul>
                    {result.issues.map((issue, index) => (
                        <li key={index} style={{ color: 'red' }}>
                            Line {issue.line}: {issue.issue}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No issues found</p>
            )}
            <button onClick={handleExportPDF}>Export as PDF</button>
        </div>
    );
};

export default AnalysisResult;
