import React, { useEffect, useRef } from 'react';
import hljs from 'highlight.js';
import 'highlight.js/styles/github-dark.css';
import axios from 'axios';

const AnalysisResult = ({ result }) => {
    const codeRef = useRef();

    useEffect(() => {
        if (codeRef.current) {
            hljs.highlightElement(codeRef.current);
        }
    }, [result]);

    if (!result) {
        return null;
    }

    const getHighlightedCode = () => {
        const lines = result.fileContent.split('\n').map((line, index) => {
            const lineNumber = index + 1;
            const lineContent = result.issues.some(issue => issue.line === lineNumber) ?
                `<span style="background-color: red;">${line}</span>` :
                line;
            return `<span>${lineNumber.toString().padStart(3, ' ')}: ${lineContent}</span>`;
        });
        return lines.join('\n');
    };

    const handleExportPDF = async () => {
        try {
            const response = await axios.post('http://localhost:3001/generate-pdf', { result }, {
                responseType: 'blob'
            });
            const url = window.URL.createObjectURL(new Blob([response.data]));
            const link = document.createElement('a');
            link.href = url;
            link.setAttribute('download', 'analysis_result.pdf');
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (error) {
            console.error('Error generating PDF', error);
        }
    };

    return (
        <div>
            <h2>Analysis Result</h2>
            <pre>
                <code ref={codeRef} className="hljs" dangerouslySetInnerHTML={{ __html: getHighlightedCode() }} />
            </pre>
            <h3>Issues:</h3>
            {result.issues.length > 0 ? (
                <ul>
                    {result.issues.map((issue, index) => (
                        <li key={index} style={{ color: 'red' }}>
                            Line {issue.line}: {issue.issue}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No issues found</p>
            )}
            <button onClick={handleExportPDF}>Export as PDF</button>
        </div>
    );
};

export default AnalysisResult;
