import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import '../style/Statistics.css';

const Statistics = () => {
    const [statistics, setStatistics] = useState([]);

    useEffect(() => {
        fetchStatistics();
    }, []);

    const fetchStatistics = async () => {
        try {
            const response = await axios.get('http://localhost:3001/statistics');
            setStatistics(response.data);
        } catch (error) {
            console.error('Error fetching statistics:', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:3001/statistics/${id}`);
            fetchStatistics();
        } catch (error) {
            console.error('Error deleting statistic:', error);
        }
    };

    const handleClearAll = async () => {
        try {
            await axios.delete('http://localhost:3001/statistics');
            fetchStatistics();
        } catch (error) {
            console.error('Error clearing statistics:', error);
        }
    };

    return (
        <div className="statistics-container">
            <h2>Analysis Statistics</h2>
            <table className="statistics-table">
                <thead>
                <tr>
                    <th>ID</th>
                    <th>File Name</th>
                    <th>Language</th>
                    <th>Vulnerabilities Detected</th>
                    <th>Analysis Date</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody>
                {statistics.map((stat) => (
                    <tr key={stat.id}>
                        <td>{stat.id}</td>
                        <td>{stat.file_name}</td>
                        <td>{stat.language}</td>
                        <td>{stat.vulnerabilities_detected}</td>
                        <td>{stat.analysis_date}</td>
                        <td>
                            <button onClick={() => handleDelete(stat.id)}>Delete</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
            <div className="actions-container">
                <Link to="/">
                    <button className="action-button">Home</button>
                </Link>
                <button className="action-button" onClick={handleClearAll}>Clear All</button>
            </div>
            <button className="back-button">Back</button>
        </div>
    );
};

export default Statistics;
