import React, { useEffect, useRef } from 'react';
import hljs from 'highlight.js';
import 'highlight.js/styles/github-dark.css';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import styles from '../style/AnalysisResult.module.css';  // Імпорт модульних стилів

const AnalysisResult = ({ result }) => {
    const codeRef = useRef();

    useEffect(() => {
        if (codeRef.current) {
            hljs.highlightElement(codeRef.current);
        }
    }, [result]);

    if (!result) {
        return null;
    }

    const getHighlightedCode = () => {
        const lines = result.rawContent.split('\n').map((line, index) => {
            const lineNumber = index + 1;
            const lineContent = result.issues.some(issue => issue.line === lineNumber) ?
                `<span class="${styles.highlight}">${line}</span>` :
                line;
            return `<span class="${styles.lineNumber}">${lineNumber.toString().padStart(3, ' ')}:</span> ${lineContent}`;
        });
        return lines.join('\n');
    };

    const handleExportPDF = () => {
        const doc = new jsPDF();
        doc.setFontSize(18);
        doc.text('Analysis Result', 14, 22);
        doc.setFontSize(12);
        doc.text('Code:', 14, 30);

        const splitCode = doc.splitTextToSize(result.rawContent, 180);
        const lineHeight = doc.getTextDimensions(splitCode[0]).h;

        splitCode.forEach((line, index) => {
            const y = 36 + (index * lineHeight);
            doc.text(`${index + 1}`.padStart(3, ' '), 10, y);
            doc.text(line, 20, y);
        });

        if (result.issues.length > 0) {
            doc.setFontSize(12);
            doc.text('Issues:', 14, 36 + splitCode.length * lineHeight + 10);
            result.issues.forEach((issue, index) => {
                doc.text(
                    `Line ${issue.line}: ${issue.issue}`,
                    14,
                    42 + splitCode.length * lineHeight + 10 + index * lineHeight
                );
            });
        } else {
            doc.setFontSize(12);
            doc.text('No issues found', 14, 36 + splitCode.length * lineHeight + 10);
        }

        doc.save('analysis_result.pdf');
    };

    return (
        <div className={styles.analysisResult}>
            <h2>Analysis Result</h2>
            <pre className={styles.codeBlock}>
                <code ref={codeRef} className="hljs" dangerouslySetInnerHTML={{ __html: getHighlightedCode() }} />
            </pre>
            <h3>Issues:</h3>
            {result.issues.length > 0 ? (
                <ul className={styles.issuesList}>
                    {result.issues.map((issue, index) => (
                        <li key={index} className={styles.issueItem}>
                            Line {issue.line}: {issue.issue}
                        </li>
                    ))}
                </ul>
            ) : (
                <p className={styles.noIssues}>No issues found</p>
            )}
            <button className={styles.exportButton} onClick={handleExportPDF}>Export as PDF</button>
        </div>
    );
};

export default AnalysisResult;
